class EcranDetails extends StatelessWidget {
  String details = "Details non disponible";
  String titre = "Titre";
  String imageURL = "";

  EcranDetails(String details, String titre, String imageURL) {
    this.details = details;
    this.titre = titre;
    this.imageURL = imageURL;
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(),
      body: Center(
        child: Column(
          children: [
            //TODO Afficher les variables sur l'ecran
            Hero(child: Image.network(imageURL, height: 200), tag: "lochness"),
            Hero(
                child: Text(
                  titre,
                  style: TextStyle(fontSize: 40, fontWeight: FontWeight.bold),
                ),
                tag: "lochnesstext"),
            Text(details),
            Padding(
              padding: const EdgeInsets.only(top: 20),
              child: FlatButton(
                child: Text('Fermer!'),
                onPressed: () {
                  Navigator.pop(context);
                },
              ),
            ),
          ],
        ),
      ),
    );
  }
}
